INPUT_STRING = ""

def isAllUnique(input):
    # we don't want to damage the input string
    input_copy = input.copy()
    for i in range(len(input_copy) - 1):
        last_char = input_copy.pop()
        if last_char in input_copy:
            return False
    return True

last_four = []

for i in range(len(INPUT_STRING)):
    char = INPUT_STRING[i]
    last_four.insert(0, char)
    if len(last_four) >= 4 and isAllUnique(last_four):
        break
    if len(last_four) > 4:
        last_four.pop()

print(f"Done. First char of marker occurs at {i}")