INPUT_STRING = "nppdvjthqldpwncqszvftbrmjlhg" #""
LENGTH_OF_TARGET = 14
OFFSET = 4

def isAllUnique(input):
    # we don't want to damage the input string
    input_copy = input.copy()
    for x in range(len(input_copy) - 1):
        last_char = input_copy.pop()
        if last_char in input_copy:
            return False
    return True

recent_chars = []

for i in range(len(INPUT_STRING)):
    char = INPUT_STRING[i]
    recent_chars.insert(0, char)
    if len(recent_chars) > LENGTH_OF_TARGET:
        del recent_chars[-1]
    if len(recent_chars) >= LENGTH_OF_TARGET and isAllUnique(recent_chars):
        break

print(f"Done. First char of marker occurs at {i+1}")